import { useState } from 'react';
import { ShoppingBag } from 'lucide-react';
import CartItem from './CartItem';
import CheckoutForm from './CheckoutForm';

interface CartItemType {
  id: number;
  name: string;
  price: number;
  quantity: number;
  image: string;
}

function CheckOut() {
  const [cartItems, setCartItems] = useState<CartItemType[]>([
    {
      id: 1,
      name: "Salsa Chili Mixes Fintual",
      price: 49.99,
      quantity: 1,
      image: "https://store.fintual.cl/cdn/shop/products/Redessociales_Cyber-22copia-2_1080x.jpg?v=1669726962"
    },
    {
      id: 2,
      name: "Smart Watch Series 5",
      price: 29.99,
      quantity: 1,
      image: "https://images.unsplash.com/photo-1546868871-7041f2a55e12?auto=format&fit=crop&q=80&w=200&h=200"
    }
  ]);

  const updateQuantity = (id: number, newQuantity: number) => {
    if (newQuantity < 1) return;
    setCartItems(items =>
      items.map(item =>
        item.id === id ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  const removeItem = (id: number) => {
    setCartItems(items => items.filter(item => item.id !== id));
  };

  const subtotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const shipping = 9.99;
  const total = subtotal + shipping;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white rounded-2xl shadow-lg p-6 md:p-8">
          <div className="flex items-center justify-between mb-8">
            <h1 className="text-2xl font-bold text-gray-900">Fintualist</h1>
            <div className="relative">
              <ShoppingBag className="w-6 h-6 text-gray-600" />
              <span className="absolute -top-2 -right-2 bg-blue-600 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                {cartItems.length}
              </span>
            </div>
          </div>

          <div className="grid md:grid-cols-5 gap-8">
            <div className="md:col-span-3">
              <div className="space-y-1">
                <h2 className="text-xl font-semibold text-gray-900">Shopping Cart</h2>
                <p className="text-sm text-gray-500">{cartItems.length} items in your cart</p>
              </div>

              <div className="mt-6 divide-y divide-gray-100">
                {cartItems.map(item => (
                  <CartItem
                    key={item.id}
                    {...item}
                    onUpdateQuantity={updateQuantity}
                    onRemove={removeItem}
                  />
                ))}
              </div>

              <div className="mt-8 space-y-4">
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600">Subtotal</span>
                  <span className="font-medium">${subtotal.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="text-gray-600">Shipping</span>
                  <span className="font-medium">${shipping.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-base font-semibold">
                  <span>Total</span>
                  <span>${total.toFixed(2)}</span>
                </div>
              </div>
            </div>

            <div className="md:col-span-2">
              <CheckoutForm />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CheckOut;
