import React, { FC } from "react";

interface PaymentMethodButton {
  id: string;
  name: string;
  icon: React.ElementType;
  description: string;
  color: string;
  selectedMethod: string | null;
  onClick: (id: string) => void;
  disabled: boolean;
}

export const PaymentMethodButton: FC<PaymentMethodButton> = ({
  color,
  description,
  icon,
  id,
  selectedMethod,
  onClick,
  name,
  disabled,
}) => {
  const Icon = icon;

  return (
    <button
      key={id}
      onClick={() => onClick(id)}
      disabled={disabled}
      className={`w-full p-4 rounded-xl border-2 transition-all duration-200 ${
        selectedMethod === id
          ? 'border-blue-500 bg-blue-50'
          : 'border-gray-200 hover:border-gray-300 bg-white'
      } ${disabled ? 'cursor-not-allowed border-gray-200 bg-gray-200' : 'cursor-pointer'}`}

    >
      <div className="flex items-center space-x-4">
        <div className={`p-2 rounded-lg ${disabled ? "bg-gray-400" : color}`}>
          <Icon className={`w-6 h-6 text-white ${disabled ?? "text-gray"}`} />
        </div>
        <div className="flex-1 text-left">
          <h3 className="font-medium text-gray-900">{name}</h3>
          <p className="text-sm text-gray-500">{description}</p>
        </div>
        <div className="w-5 h-5 rounded-full border-2 flex items-center justify-center">
          {(!disabled && selectedMethod === id) && (
            <div className="w-3 h-3 rounded-full bg-blue-500" />
          )}
        </div>
      </div>
    </button>
  );
};
